def plot_loss(train_loss_list,nitems):
    labels = ['run0', 'run1', 'run2', 'run4', 'run5']
    fig = plt.figure(figsize=(8,5))
    plt.xlim(-0.5,19.5)
    #plt.ylim(0,2)
    x_vector = np.linspace(0,20,20)
    default_x_ticks = range(len(x_vector))
    plt.title("Training loss during steps")
    plt.suptitle("Training set of" +str(nitems)+ "images")
    plt.xlabel("Steps")
    plt.ylabel("Training loss")
    colors=['green', 'magenta', 'darkblue', 'gold', 'brown']
    for j in range(len(train_loss_list)):
            plt.plot(train_loss_list[j], label =labels[j], marker='s', linewidth=2, fillstyle='none',color=colors[j])
            plt.legend(loc=1)
    plt.xticks(default_x_ticks)
    plt.grid(True)
    plt.savefig("/Users/francescoaldoventurelli/Desktop/CIRCUITSSS/Loss_CNN_during_EPOCHS.jpg",dpi=800)
    return fig


def plot_accuracy(train_acc_list,nitems):
    labels = ['run0', 'run1', 'run2', 'run4', 'run5']
    fig = plt.figure(figsize=(8,5))
    plt.xlim(-0.5,19.5)
    plt.ylim(0,1.05)
    x_vector = np.linspace(0,20,20)
    default_x_ticks = range(len(x_vector))
    plt.title("Training accuracy during steps")
    plt.suptitle("Training set of" +str(nitems)+ "images")
    plt.xlabel("Steps")
    plt.ylabel("Training accuracy")
    colors=['green', 'magenta', 'darkblue', 'gold', 'brown']

    for j in range(len(train_acc_list)):
            plt.plot(train_acc_list[j], label =labels[j], marker='o', linewidth=2, fillstyle='none', color=colors[j])
            plt.legend(loc=4)
    plt.xticks(default_x_ticks)
    plt.grid(True)
    plt.savefig('/Users/francescoaldoventurelli/Desktop/CIRCUITSSS/train_acc_CNN_during_EPOCHS.jpg',dpi=800)
