def reshaping(vector, size1, size2):
    reshaped_vector = vector.reshape(size1,size2,1)
    return reshaped_vector



def CNNModel(input_features):
    model = Sequential([
    Conv2D(1, (3,3), activation='relu', input_shape=(input_features,input_features,1)),
    MaxPool2D(pool_size=(2,2), strides=3),
    Flatten(),
    Dense(1, activation='sigmoid')])
    return model

def Summary(model):
    return model.summary()

