def TRAIN_EVAL_MODEL(num_train, features): 
    model = CNNModel(features)
    numzero = int(num_train/2)
    numone = int(num_train/2)
    train_eval=[]
    test_eval = []
    train_loss = []
    train_acc = []
    for i in myseed:
        print('Seed number:', i)
        #X_train, Y_train, X_test, Y_test = create_train_test(numzero, numone, 50,50,i)
        X_train, Y_train = make_train(numzero,numone,i)
        X_test,Y_test = make_test()

        train_galaxy = []
        for i in range(len(X_train)):
            train_galaxy.append(reshaping(X_train[i], features,features))
        train_galaxy = np.array(train_galaxy)
        test_galaxy = []
        for i in range(len(X_test)):
            test_galaxy.append(reshaping(X_test[i],features,features))
        test_galaxy = np.array(test_galaxy)
        Y_train = np.array(Y_train)
        Y_test = np.array(Y_test)
        model.compile('adam','binary_crossentropy',['accuracy'])
        history = model.fit(train_galaxy, Y_train, batch_size=1, epochs=epochs)
        train_loss.append(history.history['loss'])
        train_acc.append(history.history['accuracy'])
        accuracy_ontrain = model.evaluate(train_galaxy, Y_train, verbose=0)
        train_eval.append(accuracy_ontrain[1])
        score = model.evaluate(test_galaxy, Y_test, verbose=0)
        test_eval.append(score[1])
        ### per la curva ROC
        #y_pred_keras = model.predict(test_galaxy).ravel()
        #fpr_keras, tpr_keras, thresholds_keras = roc_curve(Y_test, y_pred_keras)
        #auc_keras = auc(fpr_keras, tpr_keras)
    return [train_loss, train_acc, train_eval, test_eval]


